import React from "react";
import Input from "../UI/Input";
import "./MealItemForm.css";
const MealItemForm = (props) => {
  return (
    <form className="form">
      {/* input form */}
      <Input
        label="Amount"
        input={{
          id: "amount",
          type: "number",
          min: "1",
          max: "5",
          step: "1",
          defaultValue: "1",
        }}
      />
      <button class="add-btn">Add</button>
    </form>
  );
};

export default MealItemForm;
